import React, { useState } from 'react';
import logo from './logo.svg';
import './App.css';

function App() {
  var [username, usernamedata] = useState(['호랑이', '사자']);
  let [isTodo] = useState(true);
  const onChangeTab = (name) => {
    console.log(name)
     if(name === "todo") {
       isTodo = true;
     }
     else if(name === "done") {
       isTodo = false;
     }
  }

  return (
    <div>
      <div className="nav-bar">
        <h3>{username[0]}님 저장소</h3>
        <ul>
          <button onClick={ onChangeTab }><li>Todo List</li></button>
          <button onClick={ onChangeTab }><li>Done List</li></button>

          {
            isTodo === true 
            ? <Todo/> 
            : <Done/>
          }
        </ul>
      </div>

    </div>
  );
}


function Todo() {
  return (
    <div>
      <div class="title">
        <h2>Todo List</h2>
      </div>
      <div class="input-box">
        <input id="input" type="text" placeholder="할 일을 입력하세요." /><button id="add">+</button>
      </div>
      <div class="list">
        <ul id="to-do-list"></ul>
      </div>
    </div>
  )
}

function Done() {
  return (
    <div>
      <div class="title">
        <h2>Done List</h2>
      </div>
      <div class="list">
        <ul id="done-list"></ul>
      </div>
    </div>
  )
}

export default App;
